import React, { useState, useContext } from "react";
import axios from "axios";
import GeneralContext from "./GeneralContext";
import "./BuyActionWindow.css";
 import { HoldingContext } from "./HoldingContext";
const BuyActionWindow = ({ uid }) => {
  const [stockQuantity, setStockQuantity] = useState(1);
  const [stockPrice, setStockPrice] = useState(0.0);


  const holdingstate=useContext(HoldingContext);

  const { closeBuyWindow } = useContext(GeneralContext);
 const handleBuyClick = async () => {
try {
  
  const response = await axios.post("http://localhost:8080/neworder", {
    name: uid,
    qty: stockQuantity,
    price: stockPrice,
    mode: "BUY",
  }).then((res)=>{
holdingstate.setAllHoldings(prev => [...prev, res.data]);

  })
  console.log("Order response:", response);  
  closeBuyWindow();
} catch (error) {
  console.error("Buy order failed:", error);  // full error object
  alert("Buy request failed. Check backend or CORS settings.");
}
 }

  const handleCancelClick = () => {
    closeBuyWindow();
  };

  return (
    <div className="container" id="buy-window" draggable="true">
      <div className="regular-order">
        <div className="inputs">
          <fieldset>
            <legend>Qty.</legend>
            <input
              type="number"
              name="qty"
              id="qty"
              value={stockQuantity}
              onChange={(e) => setStockQuantity(Number(e.target.value))}
            />
          </fieldset>
          <fieldset>
            <legend>Price</legend>
            <input
              type="number"
              name="price"
              id="price"
              step="0.05"
              value={stockPrice}
              onChange={(e) => setStockPrice(Number(e.target.value))}
            />
          </fieldset>
        </div>
      </div>

      <div className="buttons">
        <span>Margin required â‚¹140.65</span>
        <div>
          <button className="btn btn-blue" onClick={handleBuyClick}>
            Buy
          </button>
          <button className="btn btn-grey" onClick={handleCancelClick}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default BuyActionWindow;
